function navegacionFija(){const e=document.querySelector(".header"),t=document.querySelector(".sobre-festival");window.addEventListener("scroll",(function(){t.getBoundingClientRect().bottom<0?e.classList.add("fijo"):e.classList.remove("fijo")}))}function crearGaleria(){const e=document.querySelector(".galeria-imagenes");for(let t=1;t<=16;t++){const o=document.createElement("IMG");o.loading="lazy",o.width=300,o.height=200,o.src=`src/img/gallery/thumb/${t}.jpg`,o.alt=`Imagen galeria ${t}`,o.onclick=function(){mostrarImagen(t)},e.appendChild(o)}}function mostrarImagen(e){const t=document.createElement("DIV");t.classList.add("modal");const o=document.createElement("IMG");o.src=`src/img/gallery/full/${e}.jpg`,o.alt=`Imagen galeria ${e}`;const n=document.createElement("BUTTON");n.textContent="X",n.classList.add("btn-cerrar"),n.onclick=cerrarNodal,t.appendChild(o),t.appendChild(n),t.onclick=cerrarNodal;const c=document.querySelector("body");c.classList.add("overflow-hidden"),c.appendChild(t)}function cerrarNodal(){const e=document.querySelector(".modal");e.classList.add("fade-out"),setTimeout((()=>{e?.remove();document.querySelector("body").classList.remove("overflow-hidden")}),500)}function resaltarEnlace(){document.addEventListener("scroll",(function(){const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion-principal a");let o="";e.forEach((e=>{const t=e.offsetTop,n=e.clientHeight;window.scrollY>=t-n/3&&(o=e.id)})),t.forEach((e=>{e.classList.remove("activo"),e.getAttribute("href")===`#${o}`&&e.classList.add("activo")}))}))}function scrollNav(){document.querySelectorAll(".navegacion-principal a").forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();document.querySelector(e.target.attributes.href.value).scrollIntoView({behavior:"smooth"})}))}))}document.addEventListener("DOMContentLoaded",(function(){navegacionFija(),crearGaleria(),resaltarEnlace(),scrollNav()}));